mapper.select:
SELECT
-- 级联表的字段 ,yonghu.username as username ,yonghu.password as password ,yonghu.name as name ,yonghu.phone as phone ,yonghu.id_number as idNumber ,yonghu.birthday_time as birthdayTime ,yonghu.bumen_types as bumenTypes ,yonghu.zhiwei_types as zhiweiTypes ,yonghu.sex_types as sexTypes ,yonghu.my_photo as myPhoto ,yonghu.nation as nation ,yonghu.politics_types as politicsTypes ,yonghu.birthplace as birthplace
FROM qingjia a left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.yonghu_id = #{params.yonghuId}
and a.onday like CONCAT('%',#{params.onday},'%')
and a.downday like CONCAT('%',#{params.downday},'%')
and a.qingjia_number >= #{params.qingjiaNumberStart}
and a.qingjia_number <= #{params.qingjiaNumberEnd}
and a.qingjia_types = #{params.qingjiaTypes}
and a.liyou_content like CONCAT('%',#{params.liyouContent},'%')
and DATE_FORMAT(a.update_time, '%Y-%m-%d %H:%T:%s') >= DATE_FORMAT(#{params.updateTimeStart}, '%Y-%m-%d %H:%T:%s')
and DATE_FORMAT(a.update_time, '%Y-%m-%d %H:%T:%s') <= DATE_FORMAT(#{params.updateTimeEnd} , '%Y-%m-%d %H:%T:%s')
and yonghu.username like CONCAT('%',#{params.username},'%')
and yonghu.password like CONCAT('%',#{params.password},'%')
and yonghu.name like CONCAT('%',#{params.name},'%')
and yonghu.phone like CONCAT('%',#{params.phone},'%')
and yonghu.id_number like CONCAT('%',#{params.idNumber},'%')
and DATE_FORMAT(yonghu.birthday_time, '%Y-%m-%d %H:%T:%s') >= DATE_FORMAT(#{params.birthdayTimeStart}, '%Y-%m-%d %H:%T:%s')
and DATE_FORMAT(yonghu.birthday_time, '%Y-%m-%d %H:%T:%s') <= DATE_FORMAT(#{params.birthdayTimeEnd} , '%Y-%m-%d %H:%T:%s')
and yonghu.bumen_types = #{params.bumenTypes}
and yonghu.zhiwei_types = #{params.zhiweiTypes}
and yonghu.sex_types = #{params.sexTypes}
and yonghu.nation like CONCAT('%',#{params.nation},'%')
and yonghu.politics_types = #{params.politicsTypes}
and yonghu.birthplace like CONCAT('%',#{params.birthplace},'%')
order by a.id desc
